INSERT INTO `bc_helpdocs` (`hid`, `sid`, `name`, `description`, `document`, `usetranslation`, `enabled`, `disporder`) VALUES
(1, 1, 'عضویت کاربر', 'مزایای عضویت در این تالار.', 'برای استفاده از بعضی از بخش‌های این تالار نیاز است که در تالار عضو شوید. عضویت برای همه افراد آزاد می‌باشد و این عمل چند دقیقه بیشتر طول نمی‌کشد.<br />\r\n<br />\r\nزمانی که شما در تالار عضو می‌شوید می‌توانید برای سایر اعضا پیام بفرستید یا موضوع و مشکل خود را ارسال نمایید. ضمنا می‌توانید پروفایل ویژه خود را نیز داشته باشید.<br />\r\n<br />\r\nبرخی از مزایای عضویت در تالار عبارتند از:<br /> اشتراک در موضوع‌ها, علاقمندی‌ها, تغییر قالب تالار, دستیابی به ویرایشگر مخصوص خود و ارسال رایانامه برای اعضا.', 1, 1, 1),
(2, 1, 'بروز رسانی پروفایل', 'تغییر اطلاعات جاری مربوط به شما.', 'بعضی مواقع ممکن است که شما بخواهید اطلاعات پروفایل خود را بروز رسانی نمایید .اطلاعاتی مانند: اطلاعات مربوط به مسنجر, رمز ورود, یا تغییر آدرس رایانامه.<br> شما می‌توانید تمام اطلاعات کاربری خود را از طریق کنترل پنل ویژه خود در تالار تغییر دهید. برای دسترسی به کنترل پنل, تنها کافی است که بر روی عنوان کنترل پنل کاربر در منوی بالایی صفحه کلیک نمایید. از آنجا "ویرایش مشخصات" را انتخاب کرده و موارد مد نظر خود را تغییر دهید, سپس موافقت خود را با انجام تغییرات اعلام نمایید تا تغییرات جدید برای شما اجرا شود.', 1, 1, 2),
(3, 1, 'استفاده از کوکی‌ها در مای‌بی‌بی', 'این تالار از کوکی‌ها برای ثبت اطلاعات عضویت و ورود و خروج شما استفاده می‌کند.', 'در صورتی که شما در سایت عضو شده باشید اطلاعات مربوط به عضویت و ورود و خروج شما از طریق کوکی‌ها انجام می‌شود، ضمنا اطلاعات مربوط به آخرین بازدید شما از تالار هم از طریق کوکی‌ها ثبت می‌شود.<br />\r\n<br />\r\nکوکی‌ها اسناد متنی کوچکی هستند که در کامپیوتر شما ذخیره می‌شوند; استفاده از کوکی‌ها فقط برای موارد امنیتی می‌باشد و استفاده دیگری از آنها نمی‌شود.<br />\r\n<br />\r\nکوکی‌ها ضمنا اطلاعات مربوط به موضوع‌های خوانده شده توسط شما را نیز ثبت می‌نماید.<br />\r\n<br />\r\nبرای حذف تمام کوکی‌های ذخیره شده توسط این تالار می‌توانید <a href="misc.php?action=clearcookies&amp;my_post_key={1}">اینجا</a>کلیک نمایید.', 1, 1, 3),
(4, 1, 'ورود و خروج', 'روش ورود و خروج از تالار.', 'وقتی شما برای یک بار وارد این تالار می‌شوید کوکی‌ها فعال می‌شوند واطلاعات ورود شما را ثبت می‌کنند و برای بار بعدی نیازی به وارد کردن رمز ورود و نام‌کاربری نمی‌باشد و شما به صورت خودکار وارد تالار خواهید شد. با استفاده از کوکی‌ها غیر از شما هیچ کس دیگری نمی‌تواند وارد اشتراک کاربری شما در این تالار شود.<br /> درصورتی کوکی‌ها برای شما هنگام بازدید اجرا نمی‌شوند که بعد از ورود به تالار از حساب کاربری خود خارج شوید.\r\n<br /><br />برای ورود فقط کافی است که بر روی گزینه ورود در بالا تالار کلیک نمایید. برای خروج نیز بر روی گزینه خروج در همان منوی بالای صفحه کلیک نمایید. در صورتی که عملیات خروج انجام نشد کوکی‌ها را از روی سیستم خود پاک کنید.', 1, 1, 4),
(5, 2, 'ارسال یک موضوع جدید', 'شروع یک موضوع جدید در تالار.', 'در صورتی که شما قبلا در تالار عضو شده اید و می‌خواهید یک موضوع جدید را ارسال نمایید، بسیار ساده است.فقط کافی است که در تالار مورد نظر خود بر روی دکمه "موضوع جدید" کلیک نمایید. لزوما شما دسترسی به تمام تالارها برای ارسال مطلب یا جواب ندارید. ممکن است که بعضی از تالارها برای مدیران باشد و یا اینکه مدیریت کل تالار آن را برای اعضای عادی قفل کرده باشد و فقط قابل مشاهده باشد.', 1, 1, 1),
(6, 2, 'ارسال یک پاسخ', 'پاسخ به موضوع‌ها در تالار.', 'ممکن است که شما علاقمند به دادن پاسخ به یک موضوع در تالارها باشید.برای اینکار می‌توانید بر روی دکمه. "ارسال پاسخ" در همان موضوع کلیک کرده و جواب خود را ارسال نمایید. ضمنا می‌توانید از قسمت پایین هر تالار از کادر ارسال پاسخ سریع نیز استفاده نمایید.\r\n<br /><br />در صورتی که برخی از جواب‌ها نامناسب باشد مدیران تالار این اجازه را دارند که ارسال‌های شما را ویرایش یا حذف نمایند.', 1, 1, 2),
(7, 2, 'مای‌کد', 'آشنایی با امکانات مای‌کد برای بهتر شدن ارسال‌های شما.', '<span dir="rtl">نوع تگ‌ها و نمونه خروجی را در زیر هر یک از تگ‌ها مشاهده می‌کنید.</span>\r\n<p><br />[b]متن ضخیم[/b]<br />&nbsp;&nbsp;&nbsp;<b>متن ضخیم</b>\r\n<p>[i]متن مورب[/i]<br />&nbsp;&nbsp;&nbsp;<i>متن مورب</i>\r\n<p>[u]متن زیر خط دار[/u]<br />&nbsp;&nbsp;&nbsp;<u>متن زیر خط دار</u>\r\n<p>[s]متن باطل شده[/s]<br />&nbsp;&nbsp;&nbsp;<strike>متن باطل شده</strike>\r\n<p><br />[url]فرا پیوند[/url]<br />&nbsp;&nbsp;&nbsp;<a href="http://www.my-bb.ir/">http://www.my-bb.ir</a>\r\n<p>[url=http://www.my-bb.ir]my-bb.ir[/url]<br />&nbsp;&nbsp;&nbsp;<a href="http://www.my-bb.ir/">my-bb.ir</a>\r\n<p>[email]info@my-bb.ir[/email]<br />&nbsp;&nbsp;&nbsp;<a href="mailto:info@my-bb.ir">info@my-bb.ir</a>\r\n<p>[email=info@my-bb.ir]رایانامه به مترجم[/email]<br />&nbsp;&nbsp;&nbsp;<a href="mailto:info@my-bb.ir">رایانامه به مترجم</a>\r\n<p>[email=info@my-bb.ir?subject=mybb-translate]رایانامه با موضوع به مترجم[/email]<br />&nbsp;&nbsp;&nbsp;<a href="mailto:info@my-bb.ir?subject=mybb-persian-translate">رایانامه با موضوع به مترجم</a>\r\n<p><br />[quote]نقل قول[/quote]<br />&nbsp;&nbsp;&nbsp;<quote>نقل قول</quote>\r\n<p>[code]خروجی کد[/code]<br />&nbsp;&nbsp;&nbsp;<code>خروجی کد</code>\r\n<p><br />[img]http://www.php.net/images/php.gif[/img]<br />&nbsp;&nbsp;&nbsp;<img src="http://www.php.net/images/php.gif">\r\n<p>[img=50x50]http://www.php.net/images/php.gif[/img]<br />&nbsp;&nbsp;&nbsp;<img src="http://www.php.net/images/php.gif" width="50" height="50" />\r\n<p><br />[color=red]متن قرمز[/color]<br />&nbsp;&nbsp;&nbsp;<font color="red">متن قرمز</font>\r\n<p>[size=3]اندازه متن[/size]<br />&nbsp;&nbsp;&nbsp;<font size="3">اندازه متن</font>\r\n<p>[font=Tahoma]نوع فونت[/font]<br />&nbsp;&nbsp;&nbsp;<font face="Tahoma">نوع فونت</font>\r\n<p><br />[align=center]قرارگیری در مرکز[/align]<div align="center">قرارگیری در مرکز</div>\r\n<p>[align=right]قرار گیری در راست[/align]<div align="right">قرار گیری در راست</div>\r\n<p>